import React, { useState } from 'react';
import { Container, Header, UserDetails, Avatar, UserFullName, PostDate, ContentWrapper, UserActions, ActionButton, Label, Count, ActionUserList } from './Post.style';
import { users } from '../../data/users';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEllipsisV, faHeart, faComments, faRetweet } from "@fortawesome/free-solid-svg-icons";

const Post = ({postData}) => {
   const [hasInteracted, setHasInteracted] = useState({
     like: false,
     share: false,
   })
   
    function getAuthorData(id) {
      return users.find((user) => user.id === id)
    }

    return (
      <Container>
        <Header>
           <UserDetails>
             <Avatar src={getAuthorData(postData.author).avatar.small}/>
             <div>
                 <UserFullName>
                     {`${getAuthorData(postData.author).first_name} 
                     ${getAuthorData(postData.author).last_name}`}
                 </UserFullName>
                 <PostDate>{postData.date}</PostDate>
             </div>
           </UserDetails>
           <FontAwesomeIcon icon={faEllipsisV} />
        </Header> 
        <ContentWrapper>
            {postData.content.text !== undefined &&
             (<p>{postData.content.text}</p>)}
            {postData.content.video !== undefined && 
             (<video src={postData.content.video} controls/>)}
            {postData.content.image.length > 0 && 
             postData.content.image.map((source, index) => (<img src={source} key={index} alt={`${getAuthorData(postData.author).first_name} 
             ${getAuthorData(postData.author).last_name}`} /> ))} 
        </ContentWrapper>

        <UserActions>
          <ActionButton interacted={hasInteracted.like} onClick={() => setHasInteracted({ 
            ...hasInteracted,
            like: !hasInteracted.like
          })}>
            {postData.likes.length > 0 && (
              <ActionUserList>
              {postData.likes.map((user, index) => {
                const currentUser = getAuthorData(user);
                return ( 
                  <span key={index}>
                    {currentUser.first_name} {currentUser.last_name}
                  </span>
                );
              })}
            </ActionUserList>
            )}
            <FontAwesomeIcon icon={faHeart} />
            <Label>Likes</Label>
            {postData.likes.length > 0 && (<Count>{postData.likes.length}</Count>)}
          </ActionButton>

          <ActionButton>
            {postData.comments.length > 0 && (
              <ActionUserList>
              {postData.comments.map((comment, index) => {
                const currentUser = getAuthorData(comment.user);
                return ( 
                  <span key={index}>
                    {currentUser.first_name} {currentUser.last_name}
                  </span>
                );
              })}
            </ActionUserList>
            )}
            <FontAwesomeIcon icon={faComments} />
            <Label>Comment</Label>
            {postData.comments.length > 0 && (<Count>{postData.comments.length}</Count>)}
          </ActionButton>

          <ActionButton interacted={hasInteracted.share} onClick={() => setHasInteracted({
            ...hasInteracted,
            share: !hasInteracted.share
          })}>
           {postData.shares.length > 0 && (
              <ActionUserList>
              {postData.shares.map((share, index) => {
                const currentUser = getAuthorData(share);
                return ( 
                  <span key={index}>
                    {currentUser.first_name} {currentUser.last_name}
                  </span>
                );
              })}
            </ActionUserList>
           )}
            <FontAwesomeIcon icon={faRetweet} />
            <Label>Shares</Label>
            {postData.shares.length > 0 && (<Count>{postData.shares.length}</Count>)}
          </ActionButton>
        </UserActions>
      </Container>
    );
};

export default Post;
