import React from 'react';
import { Container } from './Post.style';
import { users } from '../../data/users';

const Post = ({postData}) => {
    function getAuthorData(id) {
        let userData = {
            first_name: "",
            last_name: "",
            avatar: ""
        }
        users.map((user) => {
            if(user.id === id) {
                userData.first_name = user.first_name;
                userData.last_name = user.last_name;
                userData.avatar = user.avatar.small;
                return userData;
            }
        });
    }   

    console.log(getAuthorData("OCsHJEfL-S"))

    return (
      <Container>
          <h1>{postData.author}</h1>
      </Container>
    );
};

export default Post;
