import React, { useState }from "react";
import { BrowserRouter as Router, Switch, Route, } from "react-router-dom";
import { data }  from './data/data';
import LoginPage from './pages/LoginPage';
import "./style.css";

function App() {
  const [account, setAccount]= useState(data.account);

  function logUserIn() {
    setAccount({
      ...account,
      loggedIn: true,
    })
  }

  return (
    <Router>
      <Switch>
        {!account.loggedIn ? (
          <Route path="/">
          <LoginPage logUserIn />
        </Route>
        ) : (
          <>
          <Route exact path="/">
          <h1>Neewsfeed</h1>
        </Route>
        <Route path="/:userId">
          <h1>User page</h1>
        </Route>
        </>
        )}
      </Switch>
    </Router>
  );
}

export default App;
