import React, { useState } from "react";
import { Container, Title, Input, Wrapper, Button, Error  } from "./LoginPage.style";

const LoginPage = ({logUserIn}) => {
  const [values, setValues] = useState({
    email: "",
    pass: "",
    error: "",
  });

  function handleEmailChange(val) {
    setValues({
      ...values,
      email: val,
      error: "",
    });
  }

  function handlePasswordChange(val) {
    setValues({
      ...values,
      pass: val,
    });
  }

  function handleSubmit() {
    if (values.email === "curs@its.ro"  && values.pass === "parola") {
      logUserIn();
    } else{
        setValues({
            ...values,
            error: "Incorrect email/password.",
        })
    }
  }

  return (
    <Container>
      <Wrapper>
        <Title>Log in</Title>
        {values.error !== ""  &&  <Error>{values.error}</Error>}
        <Input
          placeholder="Email address"
          type="email"
          value={values.email}
          onChange={(e) => handleEmailChange(e.target.value)}
          onL
        />

        <Input
          placeholder="Password"
          type="password"
          value={values.pass}
          onChange={(e) => handlePasswordChange(e.target.value)}
        />

        <Button onClick={() => handleSubmit()}>Log me in!</Button>
      </Wrapper>
    </Container>
  );
};

export default LoginPage;
