import React from 'react';
import { Container, Header, UserDetails, Avatar, UserFullName, PostDate} from './Post.style';
import { users } from '../../data/users';

const Post = ({postData}) => {
    function getAuthorData(id) {
      const usersMatchingId = users.filter((user) => user.id === id);
      return usersMatchingId[0];
    }


    return (
      <Container>
        <Header>
           <UserDetails>
             <Avatar src={getAuthorData(postData.author).avatar.small}/>
             <div>
                 <UserFullName>
                     {`${getAuthorData(postData.author)} ${getAuthorData(postData.author)}`}
                 </UserFullName>
                 <PostDate></PostDate>
             </div>
           </UserDetails>
        </Header>  
      </Container>
    );
};

export default Post;
